       title:{
           type:String,
           default:'标题'
       },
       value:{
           type:Boolean,
           default: false
       },
        hideHeader:{
            type:Boolean,
            default: false
        },
        hideFooter:{
            type:Boolean,
            default: false
        },
        showExternalCloseButton:{
            type:Boolean,
            default: false
        },

        bgColor:{
           type:String,
            default:'bg-white'
        }
    },
    computed:{

    },
    watch:{
        value(val){
            this.show = val
        }
    },
    methods:{
        hideModal(){
            this.show = false;
            this.$emit("input",this.show)
        },
        handleRefresh(){
            this.$emit("onRefresh")
        },
        onConfirm(){
            this.$emit('onConfirm')
        },
        onCancel(){
            this.$emit('onCancel')
        }

    }
}
