import {getRechargeRecord, getUsedRecord} from "../../api/orders";
import Status from "../component/status";
import {getExtensionUser} from "../../api/user";
import {dateFormat} from "../../utils/utils";

export default {
    name: "rechargeRecord",
    components: {Status},
    data(){
        return {
            list:[],
            page:1,
        }
    },
    onPullDownRefresh(){
        console.log(`调试:触发下拉` );
        this.page =1;
        this.loadData();
    },
    onReachBottom(){
        console.log(`调试:触发触底` );
        this.loadData()
    },
    async onLoad(options){
            this.loadData();
    },
   filters:{
       DateFormat(val){
           return dateFormat(new Date(val),"yyyy-MM-dd hh:mm:ss")
       },
   },
    methods:{

        async loadData({page=1}={}){
            uni.showLoading({title:'加载中...'});
            let  data = await  getExtensionUser({page:this.page});
            uni.hideLoading();
            if(data.list.length){
                if(this.page == 1){
                    this.list = data.list;
                }else{
                    this.list = [...this.list,...data.list];

                }
                this.page = this.page + 1;



            }else{
                uni.showToast({
                    title:"已经到底了",
                    icon:'none'
                })
            }

            console.log(`调试:使用记录查询结果`, this.list)
        }
    }
}
