import Vue from 'vue'
export default {
  onLaunch: function() {
    console.log("App Launch");
    uni.getSystemInfo({
      success: function(e) {
		  console.log(`调试:获取到的系统信息`, e);
		  console.log(`调试:当前运行环境`, process.env.NODE_ENV);
		  console.log(`调试:当前运行环境`, process.env.VUE_APP_BASE_API);
          process.env.BASE_API = "www.baidu.com";
          console.log(`调试:当前ENV`,process.env);
          console.log(`调试:打印`, process)
        // #ifndef MP
        Vue.prototype.StatusBar = e.statusBarHeight;
        if (e.platform == "android") {
          Vue.prototype.CustomBar = e.statusBarHeight + 50;
        } else {
          Vue.prototype.CustomBar = e.statusBarHeight + 45;
        }
        // #endif
        // #ifdef MP-WEIXIN
        Vue.prototype.StatusBar = e.statusBarHeight;
        let custom = wx.getMenuButtonBoundingClientRect();
        Vue.prototype.Custom = custom;
        Vue.prototype.CustomBar =
          custom.bottom + custom.top - e.statusBarHeight;
        // #endif
        // #ifdef MP-ALIPAY
        // Vue.prototype.StatusBar = e.statusBarHeight;
        // Vue.prototype.CustomBar = e.statusBarHeight + e.titleBarHeight;
        // #endif
      }
    });
  },
  onShow: function() {
    console.log("App Show");
  },
  onHide: function() {
    console.log("App Hide");
  }
};
